import React from "react";
import axios from "axios";
import { CButton, CForm, CCol, CFormLabel, CFormInput } from "@coreui/react";
import "../../src/scss/Subcontent.scss";
class ShortLinkApi extends React.Component {
    state = {
        inputLink: [],
        rawLink: [],
    };

    handleChange = (event) => {
        this.setState({
            inputLink: event.target.value,
        });
    };

    async getLink(event) {
        event.preventDefault();
        let link = await this.state.inputLink;
        console.log("link = ", link);
        const res = await axios.get(
            "https://api.shrtco.de/v2/shorten?url=" + link
        );
        this.setState({
            rawLink: res.data.result ? res.data.result : [],
        });
    }

    render() {
        // eslint-disable-next-line no-unused-vars
        let { rawLink, inputLink } = this.state;

        return (
            <>
                <div className='Shorten-input'>
                    <CForm className='row g-3'>
                        <CCol xs='auto'>
                            <CFormLabel
                                htmlFor='inputPassword2'
                                className='visually-hidden '
                            >
                                Shorten a link here..
                            </CFormLabel>
                            <CFormInput
                                type='text'
                                id='inputPassword2'
                                className='link-input'
                                placeholder='Shorten a link here..'
                                value={this.state.inputLink}
                                onChange={(event) => {
                                    this.handleChange(event);
                                }}
                            />
                        </CCol>
                        <CCol xs='auto'>
                            <CButton
                                className='mb-3 link-submit '
                                onClick={(event) => this.getLink(event)}
                            >
                                Shorten it!
                            </CButton>
                        </CCol>
                    </CForm>
                </div>

                {this.state.inputLink !== "" && (
                    <div className='link-container'>
                        <div className='raw-link'>
                            <p>{rawLink.original_link}</p>
                        </div>
                        <div className='shorted'>
                            <p className='link-output'>{rawLink.short_link}</p>
                            <CButton
                                type='submit'
                                className='mb-3'
                                onClick={() => {
                                    navigator.clipboard.writeText(
                                        `http://${rawLink.short_link}`
                                    );
                                }}
                            >
                                Copy
                            </CButton>
                        </div>
                    </div>
                )}
            </>
        );
    }
}

export default ShortLinkApi;
