import React from 'react';
import axios from 'axios';
import { CButton } from '@coreui/react';
import '../../src/scss/Subcontent.scss';
class ShortLinkApi extends React.Component {
    state = {
        rawLink: []
    }

    async componentDidMount(link) {
        link = 'google.com'
        let res = await axios.get('https://api.shrtco.de/v2/shorten?url=' + link);
        this.setState({
            rawLink: res.data.result ? res.data.result : []
        })
        console.log('>>> check res: ', res.data.result)

    }



    render() {

        let { rawLink } = this.state;

        return (
            <>
                <div className='link-container'>
                    <div className="raw-link">
                        <p>{rawLink.original_link}</p>
                    </div>
                    <div className="shorted">
                        <p className='link-output'>{rawLink.short_link}</p>
                        <CButton type="submit" className="mb-3" onClick={() => { navigator.clipboard.writeText(rawLink.short_link); alert('copied') }}>
                            Copy
                        </CButton>
                    </div>
                </div>

                <div className='link-container'>
                    <div className="raw-link">
                        <p>{rawLink.original_link}</p>
                    </div>
                    <div className="shorted">
                        <p className='link-output'>{rawLink.short_link}</p>
                        <CButton type="submit" className="mb-3" onClick={() => { navigator.clipboard.writeText(rawLink.short_link); alert('copied') }}>
                            Copy
                        </CButton>
                    </div>
                </div>

                
            </>



        )
    }
}

export default ShortLinkApi;
